--查询显示每件商品的编号、名称以及利润，按利润从高到低进行排序select Wid,Wname,SalesPrice-PurchasePrice as 利润 from Wareorder by SalesPrice-PurchasePrice desc --统计每件商品的销售量，显示商品名称及销售量，按销售量从高到低排序select Wname,sum(SalesAmount) as 销售量 from Ware w,SalesInfo swhere w.Wid=s.Wid group by Wname order by sum(SalesAmount) desc--统计每类商品的销售量，显示商品类别及销售量select Cname,sum(SalesAmount) as 销售量 from Category c,Ware w,SalesInfo swhere c.Cid=w.Cid and w.Wid=s.Wid group by Cname--统计每种商品到目前的盈利总额select Wname,sum((SalesPrice-PurchasePrice)*SalesAmount) as 盈利总额from Ware w,SalesInfo swhere w.Wid=s.Wid group by Wname--收银员2号销售5件1号商品Update Ware set Amount=Amount-5 where Wname='高露洁牙膏'Insert into SalesInfo values(10010,'2010-2-13',5,1002) --按照销售总额对收银员进行排序select e.Ename,sum((SalesPrice-PurchasePrice)*SalesAmount)as 销售总额from Employee e,SalesInfo s,Ware w where e.Eid=s.Eid and w.Wid=s.Wid group by e.Ename order by 销售总额 desc--查询商品销售量排在前五名的商品名称及销量select top 5 w.Wname,sum(s.SalesAmount) 销量from ware w,SalesInfo swhere w.Wid=s.Wid group by w.Wname order by 销量 desc--统计2009年的商品销售量排在前五名的商品名称及系销量select top 5 w.Wname,sum(s.SalesAmount) 销量from ware w,SalesInfo swhere w.Wid=s.Wid and s.SalesDate between '2009-01-01' and '2010-12-31' group by w.Wname order by 销量 desc--统计每个员工在2010年的销量,显示员工名称及销售数量select e.Ename,sum(s.SalesAmount) 销量from Employee e,SalesInfo swhere e.Eid=s.Eid and s.SalesDate between '2009-01-01' and '2009-12-31' group by e.Ename order by 销量 desc--统计2009年的总利润select sum((w.SalesPrice-w.PurchasePrice)*s.SalesAmount)总利润from ware w,SalesInfo swhere w.Wid=s.Wid and s.SalesDate between '2009-01-01' and '2009-12-31'